import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [ipInput, setIpInput] = useState('');
  const [nameInput, setNameInput] = useState('');
  const [typeInput, setTypeInput] = useState('router');
  const [parentInput, setParentInput] = useState('');
  const [devices, setDevices] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [activeTab, setActiveTab] = useState('');

  const isValidIP = (ip) => {
    const regex =
      /^(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)$/;
    return regex.test(ip);
  };

  const fetchDevices = async () => {
    setLoading(true);
    try {
      const res = await axios.get('http://localhost:3001/devices');
      setDevices(res.data);
      pingAll(res.data.map((d) => d.ip));
    } catch (err) {
      console.error('Error fetching devices:', err);
    } finally {
      setLoading(false);
    }
  };

  const addDevice = async () => {
    if (!isValidIP(ipInput)) {
      setMessage('IP no vÃ¡lida');
      return;
    }

    try {
      await axios.post('http://localhost:3001/devices', {
        ip: ipInput,
        name: nameInput,
        type: typeInput,
        parent: parentInput || null,
      });
      setIpInput('');
      setNameInput('');
      setTypeInput('router');
      setParentInput('');
      setMessage('Dispositivo agregado exitosamente');
      fetchDevices();
    } catch (error) {
      console.error('Error al agregar dispositivo:', error);
      setMessage('Error al agregar dispositivo');
    }
  };

  const deleteDevice = async (ip) => {
    if (window.confirm('Â¿EstÃ¡s seguro de que quieres eliminar este dispositivo?')) {
      try {
        await axios.delete(`http://localhost:3001/devices/${ip}`);
        fetchDevices();
      } catch (err) {
        console.error('Error deleting device:', err);
        setMessage('Error al eliminar dispositivo');
      }
    }
  };

  const pingAll = async (ips) => {
    setLoading(true);
    try {
      const res = await axios.post('http://localhost:3001/ping', { devices: ips });
      setDevices((prev) =>
        prev.map((dev) => {
          const found = res.data.find((d) => d.ip === dev.ip);
          return found ? { ...dev, alive: found.alive } : dev;
        })
      );
    } catch (err) {
      console.error('Error pinging devices:', err);
      setMessage('Error al hacer ping a los dispositivos');
    } finally {
      setLoading(false);
    }
  };

  const getDeviceImage = (type) => {
    switch (type) {
      case 'router':
        return '/images/router.png';
      case 'antena':
        return '/images/antena.png';
      case 'server':
        return '/images/server.png';
      default:
        return '/images/default.png';
    }
  };

  const handleUpdateDevices = () => {
    const ips = devices.map((d) => d.ip);
    pingAll(ips);
  };

  const groupDevicesBySegment = () => {
    const groups = devices.reduce((acc, device) => {
      const segment = device.ip.split('.').slice(0, 3).join('.');
      if (!acc[segment]) {
        acc[segment] = [];
      }
      acc[segment].push(device);
      return acc;
    }, {});
    return groups;
  };

  const handleTabChange = (segment) => {
    setActiveTab(segment);
  };

  // Mostrar jerarquÃ­a de dispositivos
  const DeviceTree = ({ devices, parentId = null }) => {
    const children = devices.filter(
      (device) => (device.parent?._id || null) === parentId
    );

    if (children.length === 0) return null;

    return (
      <ul style={{
        display: 'flex',
        flexDirection: 'row',
        alignItems: 'flex-start',
        gap: 40,
        listStyle: 'none',
        padding: 0,
        position: 'relative'
      }}>
        {children.map((child) => (
          <DeviceNode key={child._id} device={child} devices={devices} />
        ))}
      </ul>
    );
  };

  const DeviceNode = ({ device, devices }) => {
    const [expanded, setExpanded] = useState(true);

    const hasChildren = devices.some(
      (d) => (d.parent?._id || null) === device._id
    );

    const toggle = () => setExpanded(!expanded);

    return (
      <li style={{ position: 'relative', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <div
          style={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            gap: 4,
            cursor: hasChildren ? 'pointer' : 'default',
            position: 'relative'
          }}
          onClick={hasChildren ? toggle : undefined}
        >
          {/* LÃ­nea hacia hijos */}
          {hasChildren && expanded && (
            <div style={{
              height: 20,
              width: 2,
              backgroundColor: '#ccc',
              position: 'absolute',
              bottom: -20,
              left: '50%',
              transform: 'translateX(-50%)'
            }} />
          )}

          <motion.span
            style={{ display: 'inline-block', width: 20 }}
            animate={{ rotate: expanded ? 90 : 0 }}
            transition={{ duration: 0.3 }}
          >
            {hasChildren && 'â–¶'}
          </motion.span>

          <img
            src={getDeviceImage(device.type)}
            alt={device.type}
            style={{ width: 24, height: 24 }}
          />
          <span style={{ textAlign: 'center' }}>
            <strong>{device.name || 'Sin nombre'}</strong><br />
            {device.ip} {device.alive ? 'ðŸŸ¢' : 'ðŸ”´'}
          </span>

          <button onClick={(e) => {
            e.stopPropagation();
            deleteDevice(device.ip);
          }}>
            Eliminar
          </button>
        </div>

        {/* Hijos */}
        <AnimatePresence initial={false}>
          {expanded && (
            <motion.div
              initial={{ opacity: 0, height: 0 }}
              animate={{ opacity: 1, height: 'auto' }}
              exit={{ opacity: 0, height: 0 }}
              transition={{ duration: 0.3 }}
              style={{ marginTop: 30 }}
            >
              <DeviceTree devices={devices} parentId={device._id} />
            </motion.div>
          )}
        </AnimatePresence>
      </li>
    );
  };

  useEffect(() => {
    fetchDevices();
    const interval = setInterval(fetchDevices, 60000);
    return () => clearInterval(interval);
  }, []);

  const deviceGroups = groupDevicesBySegment();
  const filteredDevices = activeTab
    ? devices.filter((d) => d.ip.startsWith(activeTab))
    : devices;

  return (
    <div style={{ padding: 20 }}>
      <h1>Monitor de Dispositivos</h1>

      <div style={{ marginBottom: 10 }}>
        <input
          value={ipInput}
          onChange={(e) => setIpInput(e.target.value)}
          placeholder="IP"
        />
        <input
          value={nameInput}
          onChange={(e) => setNameInput(e.target.value)}
          placeholder="Nombre"
        />
        <select value={typeInput} onChange={(e) => setTypeInput(e.target.value)}>
          <option value="router">Router</option>
          <option value="antena">Antena</option>
          <option value="server">Server</option>
        </select>
        <select value={parentInput} onChange={(e) => setParentInput(e.target.value)}>
          <option value="">Dispositivo Padre (opcional)</option>
          {devices.map((device) => (
            <option key={device._id} value={device._id}>
              {device.ip} - {device.name}
            </option>
          ))}
        </select>
        <button onClick={addDevice}>Agregar</button>
      </div>

      {message && <p>{message}</p>}

      <div>
        {Object.keys(deviceGroups).map((segment) => (
          <button
            key={segment}
            onClick={() => handleTabChange(segment)}
            style={{
              margin: 5,
              padding: 10,
              backgroundColor: activeTab === segment ? '#007bff' : '#ccc',
              color: activeTab === segment ? '#fff' : '#000',
              borderRadius: 5,
            }}
          >
            {segment}
          </button>
        ))}
      </div>

      <div>
        <DeviceTree devices={filteredDevices} />
      </div>

      {loading && <p>ðŸ•’ Cargando...</p>}

      <button onClick={handleUpdateDevices}>Actualizar Estado</button>
    </div>
  );
}

export default App;
