import React, { useEffect, useState } from 'react';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

// Componente recursivo para mostrar el 치rbol
function DeviceTree({ devices }) {
  return (
    <ul>
      {devices.map((device) => (
        <li key={device.ip}>
          <span>
            {device.ip} - {device.status}
          </span>
          {device.children && device.children.length > 0 && (
            <DeviceTree devices={device.children} />
          )}
        </li>
      ))}
    </ul>
  );
}

function App() {
  const [devices, setDevices] = useState([]);
  const [ipInput, setIpInput] = useState('');
  const [segments, setSegments] = useState([]);
  const [activeTab, setActiveTab] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchDevices();
    const interval = setInterval(fetchDevices, 60000); // refresco cada 60s
    return () => clearInterval(interval);
  }, []);

  const fetchDevices = async () => {
    try {
      setLoading(true);
      const response = await fetch('/api/devices');
      const data = await response.json();
      setDevices(data);
      const uniqueSegments = [...new Set(data.map((d) => d.ip.split('.')[2]))];
      setSegments(uniqueSegments);
      if (!activeTab && uniqueSegments.length > 0) {
        setActiveTab(uniqueSegments[0]);
      }
    } catch (error) {
      toast.error('Error al obtener dispositivos');
    } finally {
      setLoading(false);
    }
  };

  const handleAddDevice = async () => {
    if (!/^\d{1,3}(\.\d{1,3}){3}$/.test(ipInput)) {
      toast.warn('IP inv치lida');
      return;
    }

    try {
      const res = await fetch('/api/devices', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ ip: ipInput }),
      });

      if (res.ok) {
        toast.success('Dispositivo agregado');
        setIpInput('');
        fetchDevices();
      } else {
        toast.error('Error al agregar');
      }
    } catch (err) {
      toast.error('Error de red');
    }
  };

  const handleUpdateDevices = async () => {
    try {
      const res = await fetch('/api/devices/update', { method: 'POST' });
      if (res.ok) {
        toast.success('Estado actualizado');
        fetchDevices();
      } else {
        toast.error('Error al actualizar');
      }
    } catch {
      toast.error('Error al actualizar');
    }
  };

  const filteredDevices = devices.filter((d) => d.ip.split('.')[2] === activeTab);

  return (
    <div style={{ padding: 20, fontFamily: 'sans-serif' }}>
      <ToastContainer position="bottom-right" autoClose={3000} />

      <h1>Dispositivos en Red</h1>

      <div style={{ marginBottom: 20 }}>
        <input
          value={ipInput}
          onChange={(e) => setIpInput(e.target.value)}
          placeholder="Ej. 192.168.1.10"
          style={{
            padding: 8,
            marginRight: 8,
            border: '1px solid #ccc',
            borderRadius: 4,
          }}
        />
        <button
          onClick={handleAddDevice}
          style={{
            padding: '8px 16px',
            borderRadius: '6px',
            backgroundColor: '#4CAF50',
            color: '#fff',
            border: 'none',
            cursor: 'pointer',
          }}
        >
          Agregar
        </button>
      </div>

      <div style={{ marginBottom: 20 }}>
        {segments.map((segment) => (
          <button
            key={segment}
            onClick={() => setActiveTab(segment)}
            style={{
              marginRight: 8,
              padding: '8px 16px',
              borderRadius: '6px',
              backgroundColor: activeTab === segment ? '#4CAF50' : '#f0f0f0',
              color: activeTab === segment ? '#fff' : '#333',
              border: '1px solid #ccc',
              cursor: 'pointer',
            }}
          >
            {segment}
          </button>
        ))}
      </div>

      <div style={{ textAlign: 'center', marginBottom: 20 }}>
        <button
          onClick={handleUpdateDevices}
          style={{
            padding: '10px 20px',
            borderRadius: '6px',
            backgroundColor: '#2196F3',
            color: '#fff',
            border: 'none',
            cursor: 'pointer',
          }}
        >
          Actualizar Estado
        </button>
      </div>

      {loading && <p style={{ textAlign: 'center' }}>Cargando...</p>}

      <DeviceTree devices={filteredDevices} />
    </div>
  );
}

export default App;
