// App.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [ipInput, setIpInput] = useState('');
  const [devices, setDevices] = useState([]);

  const fetchDevices = async () => {
    const res = await axios.get('http://localhost:3001/devices');
    setDevices(res.data);
    pingAll(res.data.map(d => d.ip));
  };

  const addDevice = async () => {
    await axios.post('http://localhost:3001/devices', { ip: ipInput.trim() });
    setIpInput('');
    fetchDevices();
  };

  const deleteDevice = async (ip) => {
    const confirmDelete = window.confirm(`¬øEst√°s seguro de que deseas eliminar el dispositivo con IP ${ip}?`);
    if (!confirmDelete) return;
    await axios.delete(`http://localhost:3001/devices/${ip}`);
    fetchDevices();
  };

  const pingAll = async (ips) => {
    const res = await axios.post('http://localhost:3001/ping', { devices: ips });
    setDevices(devices =>
      devices.map(dev => {
        const found = res.data.find(d => d.ip === dev.ip);
        return found ? { ...dev, alive: found.alive } : dev;
      })
    );
  };

  useEffect(() => {
    fetchDevices();
    const interval = setInterval(() => fetchDevices(), 60000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{ padding: 20 }}>
      <h1>Monitor de Dispositivos</h1>
      <div style={{ marginBottom: 10 }}>
        <input
          value={ipInput}
          onChange={(e) => setIpInput(e.target.value)}
          placeholder="Ingresa IP"
        />
        <button onClick={addDevice}>Agregar</button>
      </div>
      <ul>
        {devices.map((d) => (
          <li key={d.ip}>
            {d.ip} ‚Äî {d.alive == null ? '‚è≥' : d.alive ? 'üü¢ Activo' : 'üî¥ Inactivo'}
            <button onClick={() => deleteDevice(d.ip)} style={{ marginLeft: 10 }}>‚ùå Eliminar</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
