import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [ipInput, setIpInput] = useState('');
  const [nameInput, setNameInput] = useState('');
  const [typeInput, setTypeInput] = useState('router');
  const [parentIp, setParentIp] = useState(''); // IP del dispositivo padre
  const [devices, setDevices] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [activeTab, setActiveTab] = useState('');

  // Validaci√≥n b√°sica de IP
  const isValidIP = (ip) => {
    const regex = /^(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)$/;
    return regex.test(ip);
  };

  // Llamar al backend para obtener dispositivos
  const fetchDevices = async () => {
    setLoading(true);
    try {
      const res = await axios.get('http://localhost:3001/devices');
      setDevices(res.data);
      pingAll(res.data.map(d => d.ip));
    } catch (err) {
      console.error('Error fetching devices:', err);
    } finally {
      setLoading(false);
    }
  };

  // Agregar un dispositivo
  const addDevice = async () => {
    if (!isValidIP(ipInput)) {
      setMessage('Por favor, ingresa una IP v√°lida');
      return;
    }

    try {
      await axios.post('http://localhost:3001/devices', {
        ip: ipInput.trim(),
        name: nameInput.trim(),
        type: typeInput,
        parentIp: parentIp.trim() || null // Asignar un dispositivo padre si se elige uno
      });
      setMessage('Dispositivo agregado con √©xito!');
      setIpInput('');
      setNameInput('');
      setTypeInput('router');
      setParentIp('');
      fetchDevices();
    } catch (err) {
      setMessage('Error al agregar dispositivo.');
      console.error('Error adding device:', err);
    }
  };

  // Hacer ping a todos los dispositivos
  const pingAll = async (ips) => {
    setLoading(true);
    try {
      const res = await axios.post('http://localhost:3001/ping', { devices: ips });
      setDevices(devices =>
        devices.map(dev => {
          const found = res.data.find(d => d.ip === dev.ip);
          return found ? { ...dev, alive: found.alive } : dev;
        })
      );
    } catch (err) {
      console.error('Error pinging devices:', err);
    } finally {
      setLoading(false);
    }
  };

  // Obtener imagen seg√∫n el tipo de dispositivo
  const getDeviceImage = (type) => {
    const deviceImages = {
      router: '/images/router.png',
      antena: '/images/antena.png',
      server: '/images/server.png',
      default: '/images/default.png',
    };
    return deviceImages[type] || deviceImages.default;
  };

  // Agrupar los dispositivos por segmento de IP
  const groupDevicesBySegment = () => {
    const groups = devices.reduce((acc, device) => {
      const segment = device.ip.split('.').slice(0, 3).join('.');
      if (!acc[segment]) {
        acc[segment] = [];
      }
      acc[segment].push(device);
      return acc;
    }, {});

    return groups;
  };

  // Obtener dispositivos hijos de un dispositivo espec√≠fico
  const getChildren = (parentIp) => {
    return devices.filter(dev => dev.parentIp === parentIp);
  };

  useEffect(() => {
    fetchDevices();
    const interval = setInterval(() => fetchDevices(), 20000);
    return () => clearInterval(interval);
  }, []);

  const deviceGroups = groupDevicesBySegment();

  // Funci√≥n para mostrar dispositivos de manera jer√°rquica
  const renderDeviceHierarchy = (device, level = 0) => {
    return (
      <div 
        style={{
          marginLeft: `${level * 30}px`, // Aqu√≠ se maneja la indentaci√≥n para el efecto de cascada
          marginBottom: '10px',
          display: 'flex',
          alignItems: 'center',
          backgroundColor: '#f0f0f0', // Fondo para hacer resaltar cada nivel
          padding: '5px',
          borderRadius: '5px',
        }}
      >
        <img
          src={getDeviceImage(device.type)}
          alt={device.type}
          style={{ width: 30, height: 30, marginRight: 10 }}
        />
        <span> 
          {device.name} ‚Äî {device.ip} ‚Äî {device.alive == null ? '‚è≥' : device.alive ? 'üü¢ Activo' : 'üî¥ Inactivo'} 
        </span>

        {/* Dispositivos hijos */}
        {getChildren(device.ip).length > 0 && (
          <div>
            {getChildren(device.ip).map(child => renderDeviceHierarchy(child, level + 1))}
          </div>
        )}

        <button onClick={() => deleteDevice(device.ip)} style={{ marginLeft: 10 }}>‚ùå Eliminar</button>
      </div>
    );
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>Monitor de Dispositivos</h1>

      <div style={{ marginBottom: 10 }}>
        <input
          value={ipInput}
          onChange={(e) => setIpInput(e.target.value)}
          placeholder="Ingresa IP"
        />
        <input
          value={nameInput}
          onChange={(e) => setNameInput(e.target.value)}
          placeholder="Ingresa Nombre del Dispositivo"
        />
        <select value={typeInput} onChange={(e) => setTypeInput(e.target.value)}>
          <option value="router">Router</option>
          <option value="antena">Antena</option>
          <option value="server">Server</option>
        </select>

        {/* Selector de dispositivo padre */}
        <select value={parentIp} onChange={(e) => setParentIp(e.target.value)}>
          <option value="">Selecciona un dispositivo padre (opcional)</option>
          {devices.map((dev) => (
            <option key={dev.ip} value={dev.ip}>{dev.name} - {dev.ip}</option>
          ))}
        </select>

        <button onClick={addDevice}>Agregar</button>
      </div>

      {message && <p>{message}</p>}

      <div>
        {/* Pesta√±as para cada segmento de IP */}
        {Object.keys(deviceGroups).map(segment => (
          <button
            key={segment}
            onClick={() => setActiveTab(segment)}
            style={{
              margin: 5,
              padding: 10,
              backgroundColor: activeTab === segment ? '#007bff' : '#ddd',
              color: activeTab === segment ? '#fff' : '#000',
              borderRadius: 5
            }}
          >
            {segment}
          </button>
        ))}
      </div>

      {/* Mostrar dispositivos jer√°rquicamente */}
      <div>
        {activeTab && (
          <div>
            {deviceGroups[activeTab].map((dev) => renderDeviceHierarchy(dev))}
          </div>
        )}
      </div>

      {loading && <p>üïí Actualizando estado de dispositivos...</p>}

      {/* Bot√≥n para actualizar manualmente los dispositivos */}
      <button onClick={() => pingAll(devices.map(d => d.ip))}>Actualizar Todos</button>
    </div>
  );
}

export default App;
